package ch02.sec02;

/**
 * 
 */
public class IntegerLiteralExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 0b, 0B가 붙으면 2진수
		int var1 = 0b1011;
		
		// 0이 붙으면 8진수
		int var2 = 0206;
		
		// 10진수
		int var3 = 300;
		
		// 0x, 0X가 붙으면 16진수
		int var4 = 0xB3;
		
		System.out.println("2진수 var1 = " + var1);
		System.out.println("8진수 var2 = " + var2);
		System.out.println("10진수 var3 = " + var3);
		System.out.println("16진수 var4 = " + var4);
		
	} // main 끝

} // IntegerLiteralExample 끝
