package thisisjava.ch04.sec04;


/**
 * 
 * for문 예제
 * 
 */
public class MultiplicationTableExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 10번 반복
		for(int i = 1; i <= 10; i++) {
			
			System.out.print(i + " ");
			
		} // for 종료
		
	} // main 끝

} // class 끝
