package thisisjava.ch04.sec05;

/**
 * 
 * while 예제
 * 
 */
public class PrintFrom1To10Example {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		
		// i가 10보다 작거나 같은 동안에 반복
		while (i <= 10) {
			
			System.out.println(i + " ");
			i++;
			
		}
		
	} // main 끝

} // class 끝
