package thisisjava.practice.p_250228;
/**
 * 
 */
public class P_250228_2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 정수로 선언시 int형이 기본
		int i = 10;
		
		// 실수일 경우 double형
		double d = 1.;
		
		// 숫자뒤에 F가 붙었으므로 float형
		float f = 6F;
		
		// 숫자 뒤에 d가 붙었으므로 double형
		double dd = 23d;
		
		// E는 지수 표기 법으로 14의 10승을 의미함
		// 지수가 포함된 큰 수는 double형으로 선언
		double ddd = 14E10;
		
		// ''안에 있는 문자 한개는 char형
		char  ch = 'a';

	} // main 끝

} // class 끝
