	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 5;
		double b = 5.1;
		String st = "hello";

		// 10.1hello
		System.out.println(a + b + st);

		// hello5.15
		System.out.println(st + b + a);
		
		// a + b = 10.1hello
		System.out.println("a + b = " +(a + b) + st);

	} // main 끝

