package thisisjava.practice.p_250304;

/**
 * 
 * 출력 연습
 * 
 */
public class P_250304_55 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum = 0;
		sum = sum + 1;
		
		// 1
		System.out.println(sum);
		
		sum = sum + 2;
		
		// 3
		System.out.println(sum);
		
		sum = sum + 3;
		
		// 6
		System.out.println(sum);
		
		int a = 5;
		sum = a + 5;
		
		// 10
		System.out.println(sum);
		
		a = 7;
		sum = sum + a;
		
		// 17
		System.out.println(sum);
		
	} // main 끝

} // class 끝
